(()=>{"use strict";(t=>{const e=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),s=document.getElementById("timer-seconds");let m;const r=()=>{let e=(new Date(t).getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,seconds:Math.floor(e%60),minutes:Math.floor(e/60%60),hours:Math.floor(e/60/60%24),days:Math.floor(e/60/60/24)}};m=setInterval((()=>{r().timeRemaining>0?(()=>{let t=r();e.textContent=t.days<10?`0${t.days}`:t.days,n.textContent=t.hours<10?`0${t.hours}`:t.hours,o.textContent=t.minutes<10?`0${t.minutes}`:t.minutes,s.textContent=t.seconds<10?`0${t.seconds}`:t.seconds})():(clearInterval(m),e.textContent="00",n.textContent="00",o.textContent="00",s.textContent="00")}),1e3)})("1 may 2022")})();